import{_ as e,c as i,j as t,a as o,G as r,a2 as n,B as s,o as d}from"./chunks/framework.CdbxnhrM.js";const q=JSON.parse('{"title":"📰 의결정족수","description":"의결정족수는 국회나 기타 의사결정 기구에서 특정 안건을 통과시키기 위해 필요한 최소한의 찬성표 수를 말한다. 헌법과 법률에 따라 의결 대상과 상황에 따라 다른 기준이 적용된다.","frontmatter":{"description":"의결정족수는 국회나 기타 의사결정 기구에서 특정 안건을 통과시키기 위해 필요한 최소한의 찬성표 수를 말한다. 헌법과 법률에 따라 의결 대상과 상황에 따라 다른 기준이 적용된다.","head":[["meta",{"name":"keywords","content":"의결정족수, 의사결정, 헌법, 법률"}],["meta",{"property":"og:title","content":"의결정족수"}],["meta",{"property":"og:description","content":"의결정족수는 국회나 기타 의사결정 기구에서 특정 안건을 통과시키기 위해 필요한 최소한의 찬성표 수를 말한다. 헌법과 법률에 따라 의결 대상과 상황에 따라 다른 기준이 적용된다."}],["meta",{"property":"og:image","content":"/images/quant-haun.svg"}],["meta",{"property":"og:url","content":"https://quant-haunblog.github.io/feed/social/quorum-for-decision-making"}]],"outline":[2,3]},"headers":[],"relativePath":"feed/social/quorum-for-decision-making.md","filePath":"ko/feed/social/quorum-for-decision-making.md","lastUpdated":null}'),u={name:"feed/social/quorum-for-decision-making.md"};function c(h,a,g,m,p,b){const l=s("AdsInarticle");return d(),i("div",null,[a[0]||(a[0]=t("h1",{id:"📰-의결정족수",tabindex:"-1"},[o("📰 의결정족수 "),t("a",{class:"header-anchor",href:"#📰-의결정족수","aria-label":'Permalink to "📰 의결정족수"'},"​")],-1)),a[1]||(a[1]=t("p",null,"사회 뉴스 | 2024.12.28",-1)),a[2]||(a[2]=t("h2",{id:"📌-헌정사상-최초-대통령-권한대행-탄핵안-가결-정족수-논란-지속",tabindex:"-1"},[o("📌 ‘헌정사상 최초’ 대통령 권한대행 탄핵안 가결…정족수 논란 지속 "),t("a",{class:"header-anchor",href:"#📌-헌정사상-최초-대통령-권한대행-탄핵안-가결-정족수-논란-지속","aria-label":'Permalink to "📌 ‘헌정사상 최초’ 대통령 권한대행 탄핵안 가결…정족수 논란 지속"'},"​")],-1)),a[3]||(a[3]=t("p",null,"한덕수 대통령 권한대행 탄핵소추안이 국회 본회의에서 재적의원 과반수 찬성으로 가결되며 헌정사상 초유의 상황이 발생했다. 의결 과정에서 적용된 정족수 기준을 둘러싼 여야 간 논란이 여전히 지속되고 있다.",-1)),a[4]||(a[4]=t("p",null,"국회의장은 국무총리에 대한 탄핵안으로 과반수 정족수를 적용했으나, 여당은 대통령 권한대행으로서 3분의 2 찬성이 필요하다고 반발했다. 최상목 경제부총리가 권한대행직을 승계하며 국정 운영을 이어가고 있으나 정족수 논란과 국정 공백 우려가 커지고 있다.",-1)),r(l),a[5]||(a[5]=n('<h2 id="_1️⃣-정의" tabindex="-1">1️⃣ 정의 <a class="header-anchor" href="#_1️⃣-정의" aria-label="Permalink to &quot;1️⃣ 정의&quot;">​</a></h2><p>의결정족수는 국회나 기타 의사결정 기구에서 특정 안건을 통과시키기 위해 필요한 최소한의 찬성표 수를 말한다. 의결정족수는 헌법과 법률에 따라 의결 대상과 상황에 따라 다른 기준이 적용된다.</p><h3 id="💡-국회-의결정족수" tabindex="-1">💡 국회 의결정족수 <a class="header-anchor" href="#💡-국회-의결정족수" aria-label="Permalink to &quot;💡 국회 의결정족수&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">국회의 의결은 헌법과 국회법에 따라 다음과 같은 정족수를 충족해야 한다.</p><ul><li><strong>일반 법안 및 예산안 의결</strong>: 재적 의원 과반수 출석과 출석 의원 과반수 찬성으로 의결된다.</li><li><strong>헌법 개정</strong>: 재적 의원 3분의 2 이상 찬성으로 의결된다. 이후 국민투표에서 과반수 찬성을 얻어야 확정된다.</li><li><strong>탄핵 소추</strong>: 재적 의원 과반수 발의 및 재적 의원 3분의 2 이상 찬성으로 가결된다.</li><li><strong>국회의장 선출</strong>: 재적 의원 과반수 출석과 출석 의원 과반수 찬성으로 선출된다.</li></ul></div><h3 id="💡-지방의회-의결정족수" tabindex="-1">💡 지방의회 의결정족수 <a class="header-anchor" href="#💡-지방의회-의결정족수" aria-label="Permalink to &quot;💡 지방의회 의결정족수&quot;">​</a></h3><div class="warning custom-block"><p class="custom-block-title">지방자치단체의 의회도 법령에 따라 의결정족수가 다르다.</p><ul><li><strong>일반 안건</strong>: 재적 의원 과반수 출석과 출석 의원 과반수 찬성으로 의결된다.</li><li><strong>조례 개정</strong>: 재적 의원 과반수 찬성이 필요하다.</li></ul></div><h3 id="💡-회사-주주총회-의결정족수" tabindex="-1">💡 회사 주주총회 의결정족수 <a class="header-anchor" href="#💡-회사-주주총회-의결정족수" aria-label="Permalink to &quot;💡 회사 주주총회 의결정족수&quot;">​</a></h3><div class="danger custom-block"><p class="custom-block-title">상법에 따라 주식회사의 주주총회는 다음과 같은 정족수를 요구한다.</p><ul><li><strong>일반 결의</strong>: 출석 주주의 과반수 찬성과 발행 주식 총수의 4분의 1 이상 찬성</li><li><strong>특별 결의</strong>: 출석 주주의 3분의 2 이상 찬성과 발행 주식 총수의 4분의 1 이상 찬성</li></ul></div><h3 id="💡-헌법재판소-의결정족수" tabindex="-1">💡 헌법재판소 의결정족수 <a class="header-anchor" href="#💡-헌법재판소-의결정족수" aria-label="Permalink to &quot;💡 헌법재판소 의결정족수&quot;">​</a></h3><div class="tip custom-block"><p class="custom-block-title">헌법재판소의 결정은 헌법재판소법에 따라 다음과 같은 정족수를 충족해야 한다.</p><ul><li><strong>일반 사건</strong>: <strong>재판관 7인 이상 출석</strong>과 <strong>출석 재판관 과반수 찬성</strong>.</li><li><strong>위헌 결정</strong>: <strong>재판관 6인 이상 찬성</strong> 필요.</li><li><strong>탄핵 결정</strong>: <strong>재판관 6인 이상 찬성</strong> 필요.</li></ul></div><h3 id="💡-국무회의-의결정족수" tabindex="-1">💡 국무회의 의결정족수 <a class="header-anchor" href="#💡-국무회의-의결정족수" aria-label="Permalink to &quot;💡 국무회의 의결정족수&quot;">​</a></h3><div class="warning custom-block"><p class="custom-block-title">국무회의는 헌법과 정부조직법에 따라 다음과 같은 정족수를 요구한다.</p><ul><li><strong>재적 국무위원 3분의 2 이상 출석</strong>과 <strong>출석 위원 과반수 찬성</strong>으로 의결된다.</li></ul></div><h3 id="💡-주요-포인트-요약" tabindex="-1">💡 주요 포인트 요약 <a class="header-anchor" href="#💡-주요-포인트-요약" aria-label="Permalink to &quot;💡 주요 포인트 요약&quot;">​</a></h3><div class="info custom-block"><p class="custom-block-title">의결정족수는 민주적 의사결정 과정을 보장하고, 소수에 의해 중요한 결정이 내려지는 것을 방지하는 역할을 한다.</p><ul><li><strong>과반수 출석과 과반수 찬성</strong>이 일반적인 기준.</li><li><strong>특별한 사안</strong>(헌법 개정, 탄핵, 특별 결의 등)은 강화된 정족수 요구.</li><li>회의 및 기관의 성격에 따라 다르게 적용됨.</li></ul></div><h2 id="_2️⃣-원리와-법률적-배경" tabindex="-1">2️⃣ 원리와 법률적 배경 <a class="header-anchor" href="#_2️⃣-원리와-법률적-배경" aria-label="Permalink to &quot;2️⃣ 원리와 법률적 배경&quot;">​</a></h2><p>의결정족수는 대한민국 헌법 제49조와 제65조에 따라 정해진다. 일반적인 국회 안건은 재적의원 과반수 출석과 출석 의원 과반수 찬성이 필요하다. 그러나 대통령 탄핵소추안의 경우, 헌법 제65조 제2항에 따라 재적의원 3분의 2 이상의 찬성이 필요하다. 국무총리와 같은 국무위원의 탄핵소추안은 재적의원 과반수 찬성으로 가결된다.</p><h3 id="📕-대한민국-헌법" tabindex="-1">📕 대한민국 헌법 <a class="header-anchor" href="#📕-대한민국-헌법" aria-label="Permalink to &quot;📕 대한민국 헌법&quot;">​</a></h3><ul><li><strong>제49조</strong>: 국회의 의결은 재적의원 과반수의 출석과 출석의원 과반수의 찬성으로 한다고 규정하여 기본적인 의결정족수를 설정한다.</li><li><strong>제65조</strong>: 국무총리나 국무위원의 탄핵소추 의결은 재적의원 과반수 찬성을 요구한다. 대통령 탄핵소추는 재적의원 3분의 2 이상의 찬성을 요한다. 이는 대통령의 권한이 다른 공직자보다 막대하므로 가중된 정족수를 요구하는 차별화된 기준이다.</li></ul><h3 id="📕-역사적-배경" tabindex="-1">📕 역사적 배경 <a class="header-anchor" href="#📕-역사적-배경" aria-label="Permalink to &quot;📕 역사적 배경&quot;">​</a></h3><ul><li>탄핵소추와 같은 중대한 결정에는 민주적 정당성과 공정성을 보장하기 위해 높은 기준의 의결정족수를 설정한다. 이를 통해 소수 의견에 의해 중요한 의사결정이 좌우되지 않도록 하고 다수의 동의가 수반된 결정을 보장한다. 국무총리와 국무위원 탄핵소추에 과반 찬성을 요구하는 이유는, 대통령보다 상대적으로 제한된 권한을 가지기 때문에 중립적이고 현실적인 기준을 설정한 것이다.</li><li>1987년 개정 헌법에서는 독재 및 권위주의적 결정을 방지하기 위해 의결정족수를 강화했다. 특히 대통령 탄핵소추에서 3분의 2 이상의 찬성을 요구하는 것은, 행정부 수반으로서의 막중한 권한과 그에 따르는 책임을 고려한 것이다. 의결정족수 규정은 다수결 원칙에 기반하면서도 무분별한 결정으로 인한 혼란을 방지하고, 민주적 합의를 강제하기 위한 장치다.</li></ul><h2 id="_3️⃣-영향" tabindex="-1">3️⃣ 영향 <a class="header-anchor" href="#_3️⃣-영향" aria-label="Permalink to &quot;3️⃣ 영향&quot;">​</a></h2><p>의결정족수는 중요한 의사결정에서 민주적 정당성을 부여하고 과도한 정치적 혼란을 방지한다. 다만, 지나치게 높은 정족수는 결정을 어렵게 만들어 국정 운영의 마비나 정책 지연을 초래할 가능성도 있다.</p><p>이번 한덕수 대통령 권한대행 탄핵소추에서는 과반 찬성을 기준으로 결정되었으나, 여당은 대통령 탄핵 기준인 3분의 2 적용을 주장하며 갈등이 지속되고 있다.</p><h2 id="_4️⃣-다른-용어와의-비교" tabindex="-1">4️⃣ 다른 용어와의 비교 <a class="header-anchor" href="#_4️⃣-다른-용어와의-비교" aria-label="Permalink to &quot;4️⃣ 다른 용어와의 비교&quot;">​</a></h2><h3 id="🔎-재적의원-과반수" tabindex="-1">🔎 재적의원 과반수 <a class="header-anchor" href="#🔎-재적의원-과반수" aria-label="Permalink to &quot;🔎 재적의원 과반수&quot;">​</a></h3><ul><li>전체 의원의 과반수가 찬성해야 하는 경우. 국무위원 탄핵에 주로 적용.</li></ul><h3 id="🔎-재적의원-3분의-2" tabindex="-1">🔎 재적의원 3분의 2 <a class="header-anchor" href="#🔎-재적의원-3분의-2" aria-label="Permalink to &quot;🔎 재적의원 3분의 2&quot;">​</a></h3><ul><li>전체 의원의 3분의 2 이상의 찬성이 필요한 경우. 대통령 탄핵소추안에 적용.</li></ul><h3 id="🔎-출석의원-과반수" tabindex="-1">🔎 출석의원 과반수 <a class="header-anchor" href="#🔎-출석의원-과반수" aria-label="Permalink to &quot;🔎 출석의원 과반수&quot;">​</a></h3><ul><li>실제 출석한 의원 중 과반수 찬성으로 통과. 일반적인 의사결정에 적용.</li></ul>',30))])}const f=e(u,[["render",c]]);export{q as __pageData,f as default};
